function validateJoinForms(form, id) {
    
    var firstname = $("#memberProfileEdit_" + id).find("#joinFormFirstname"),
        lastname = $("#memberProfileEdit_" + id).find("#joinFormLastname"),
        username = $("#memberProfileEdit_" + id).find("#joinFormUsername"),
        email = $("#memberProfileEdit_" + id).find("#joinFormEmail"),
        phone = $("#memberProfileEdit_" + id).find("#joinFormPhone"),
        twitter = $("#memberProfileEdit_" + id).find("#joinFormTwitter"),
        insta = $("#memberProfileEdit_" + id).find("#joinFormInsta"),
        sex = $("#memberProfileEdit_" + id).find("#joinFormSex"),
        platform = $("#memberProfileEdit_" + id).find("#joinFormPlatform"),
        country = $("#memberProfileEdit_" + id).find("#joinFormCountry"),
        specialty = $("#memberProfileEdit_" + id).find("#joinFormSpecialty"),
        role = $("#memberProfileEdit_" + id).find("#joinFormRole"),
    
        firstnameLog = $("#memberProfileEdit_" + id).find("#joinFormFirstnameLog"),
        lastnameLog = $("#memberProfileEdit_" + id).find("#joinFormLastnameLog"),
        usernameLog = $("#memberProfileEdit_" + id).find("#joinFormUsernameLog"),
        emailLog = $("#memberProfileEdit_" + id).find("#joinFormEmailLog"),
        phoneLog = $("#memberProfileEdit_" + id).find("#joinFormPhoneLog"),
        twitterLog = $("#memberProfileEdit_" + id).find("#joinFormTwitterLog"),
        instaLog = $("#memberProfileEdit_" + id).find("#joinFormInstaLog");
    
    
    firstname.removeClass("is-invalid");
    lastname.removeClass("is-invalid");
    username.removeClass("is-invalid");
    email.removeClass("is-invalid");
    phone.removeClass("is-invalid");
    twitter.removeClass("is-invalid");
    insta.removeClass("is-invalid");
    sex.removeClass("is-invalid");
    platform.removeClass("is-invalid");
    country.removeClass("is-invalid");
    specialty.removeClass("is-invalid");
    role.removeClass("is-invalid");
    
    if (firstname.val() == "") {

        firstname.addClass("is-invalid");

        firstnameLog.text("لا تترك الخانة فاضية");

        return false;

    } if (!isNaN(firstname.val())) {
        
        firstname.addClass("is-invalid");
        
        firstnameLog.text("يمكنك فقط كتابة الأحرف");
        
        return false;
        
    } if (firstname.val().length > 12 || firstname.val().length < 3) {

        firstname.addClass("is-invalid");

        firstnameLog.text("يجب ان يكون الإسم ما بين 3 - 12");

        return false;

    } if (lastname.val() == "") {

        lastname.addClass("is-invalid");

        lastnameLog.text("لا تترك الخانة فاضية");

        return false;

    } if (!isNaN(lastname.val())) {
        
        lastname.addClass("is-invalid");
        
        lastnameLog.text("يمكنك فقط كتابة الأحرف");
        
        return false;
        
    } if (lastname.val().length > 12 || lastname.val().length < 3) {

        lastname.addClass("is-invalid");

        lastnameLog.text("يجب ان يكون الإسم ما بين 3 - 12");

        return false;

    } if (username.val() == "") {

        username.addClass("is-invalid");

        usernameLog.text("لا تترك الخانة فاضية");

        return false;

    } if (!isNaN(username.val())) {
        
        username.addClass("is-invalid");
        
        usernameLog.text("يمكنك فقط كتابة الأحرف");
        
        return false;
        
    } if (username.val().length > 16 || username.val().length < 3) {

        username.addClass("is-invalid");

        usernameLog.text("يجب ان يكون الحساب ما بين 3 - 16");

        return false;

    } if (email.val() == "") {

        email.addClass("is-invalid");

        emailLog.text("لا تترك الخانة فاضية");

        return false;

    } if (email.val().length > 40 || email.val().length < 6) {

        email.addClass("is-invalid");

        emailLog.text("يجب أن يكون البريد الإلكتروني ما بين 6 - 40");

        return false;

    } if (phone.val() == "") {

        phone.addClass("is-invalid");

        phoneLog.text("لا تترك الخانة فاضية");
        
        return false;

    } if (isNaN(phone.val())) {

        phone.addClass("is-invalid");

        phoneLog.text("يمكنك فقط كتابة الأرقام");

        return false;
        
    } if (phone.val().length > 15 || phone.val().length < 6) {

        phone.addClass("is-invalid");

        phoneLog.text("يجب أن يكون رقم الهاتف ما بين 6 - 15");
        
        return false;

    } if (twitter.val() == "" && insta.val() == "") {

        twitter.addClass("is-invalid");

        twitterLog.text("إختر وحدة أو أكثر من وحدة");
        
        insta.addClass("is-invalid");

        instaLog.text("إختر وحدة أو أكثر من وحدة");

        return false;

    } if (twitter.val() != "") {
        
        if (twitter.val().charAt(0) != "@") {

            twitter.addClass("is-invalid");

            twitterLog.text("يجب أن يبدأ الحساب ب '@'");

            return false;

        } if (twitter.val().length > 18 || twitter.val().length < 3) {

            twitter.addClass("is-invalid");

            twitterLog.text("يجب أن يكون حساب تويتر ما بين 3 - 18");

            return false;

        }

        } if(insta.val() != "") { 
            
            if (insta.val().charAt(0) != "@") {

            insta.addClass("is-invalid");

            instaLog.text("يجب أن يبدأ الحساب ب '@'");

            return false;

        } if (insta.val().length > 18 || insta.val().length < 3) {

            insta.addClass("is-invalid");

            instaLog.text("يجب أن يكون حساب انستقرام ما بين 3 - 18");

            return false;

        }
                                 
    }

}

function addCommentValidate() {

    var comment = $("#comment"),

        commentLog = $("#commentLog");

    if (comment.val() == "") {

        comment.addClass("is-invalid");

        commentLog.text("لا تترك الخانة فاضية");

        return false;

    } if (comment.val().length > 350 || comment.val().length < 60) {

        comment.addClass("is-invalid");

        commentLog.text("يجب أن يكون التعليق ما بين 60 - 360");

        return false;

    }
}

